    //cuadro de dialogo para importar archivo xslx pero es el cuadro de fialogo de java
    public static void main(String[] args) throws IOException {
        // Crea un objeto FileDialog
        Frame frame = new Frame();
        FileDialog fileDialog = new FileDialog(frame, "Seleccionar archivo xlsx", FileDialog.LOAD);

        // Establece un filtro para seleccionar sólo archivos xlsx
        fileDialog.setFile("*.xlsx");

        // Muestra el cuadro de diálogo para seleccionar un archivo
        fileDialog.setVisible(true);

        // Si el usuario seleccionó un archivo, lee su contenido y lo muestra en la consola
        if (fileDialog.getFile() != null) {
            FileInputStream fileInputStream = new FileInputStream(fileDialog.getDirectory() + fileDialog.getFile());
            XSSFWorkbook workbook = new XSSFWorkbook(fileInputStream);
            fileInputStream.close();



            // Muestra el contenido del archivo en la consola
            System.out.println("Contenido del archivo:");
            System.out.println("----------------------");
            System.out.println(workbook);
        } else {
            System.out.println("No se seleccionó ningún archivo.");
        }
    }


